function BrandGrid() {
  // Precompute keys once for performance
  const brandKeys = useMemo(() => Object.keys(brandLogos), []);

  return (
    <div className="w-screen">
      <div className="w-screen grid grid-cols-4 p-8 px-0 gap-y-8">
        <div className="col-start-2 col-span-2">
          <h1 className="font-semibold text-4xl mb-5 text-black">
            Powering global brands <br /> and fast scaling startups
          </h1>
          <p className="mb-5 font-semibold text-gray-500 text-lg">
            From ambitious startups to global enterprises, Adaline helps <br />
            world-class teams iterate quickly and ship confidently.
          </p>
        </div>

        <div className="col-span-4">
          <div className="grid gap-px border border-gray-300 w-full p-1">
            <div className="grid grid-cols-4 gap-px border border-gray-300 w-full text-black">
              {Array.from({ length: 8 }).map((_, i) => (
                <LogoCell key={i} initialIndex={i % brandKeys.length} />
              ))}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

function LogoCell({ initialIndex }) {
  const [html, setHtml] = React.useState(brandLogos[initialIndex]);
  const [fading, setFading] = React.useState(false);

  const onEnter = React.useCallback(() => {
    setFading(true);
    // match ~200ms fade then swap
    setTimeout(() => {
      const randomKey = Math.floor(
        Math.random() * Object.keys(brandLogos).length
      );
      setHtml(brandLogos[randomKey]);
      setFading(false);
    }, 200);
  }, []);

  return (
    <div
      className="flex items-center justify-center h-60 border border-dashed border-gray-300"
      onMouseEnter={onEnter}
    >
      <div
        className={
          "transition-opacity duration-300 ease-in-out " +
          (fading ? "opacity-0" : "opacity-100")
        }
        dangerouslySetInnerHTML={{ __html: html }}
      />
    </div>
  );
}

export default BrandGrid;
